import React, { useState, useEffect }from 'react'
import "./photos.css"

const Photos = () => {

    const [photos, setPhotos] = useState([])
    const [page, setPage] = useState("1")

    const rickyApi = async () => {
        const res = await fetch(`https://rickandmortyapi.com/api/character?page=${page}`)
        const results = await res.json()
       setPhotos(results.results)
       console.log(results)
    }

    useEffect(() => {
        rickyApi()
    },[page])

    const handleNextPage = () => {
        page++
    }

    return (
        <div>
            <div className="photosDiv">
                {
                    photos.map((photo) => {
                        const {id, image, name } = photo
                        return (
                            <div key={id} className="photoDiv">
                                <img className="avatar" src={image}></img>
                                <p>{name}</p>
                            </div>
                        )
                    })
                    }
            </div>
            <div className="buttonDiv">
                <button className="nextButton" onClick={handleNextPage}>Next</button>
                <button className="backButton" onClick={handlePreviousPage}>Previous</button>
            </div>
        </div>
    )
}

export default Photos
