import { useState, useEffect } from "react";

function UseFetchApi(url) {

    const [photos, setPhotos] = useState([])

    const apiData = async () => {
        const res = await fetch(url)
        const { results } = await res.json()
        setPhotos(results)
    }

    useEffect(() => {
        apiData()
    },[url])

    return { photos }
}

export default UseFetchApi
