import React, { useState, useEffect } from 'react'
import "./searchCountry.css"

const SearchCountry = () => {

    const [countries, setCountries] = useState([])

        const fetchData = async() => {
            const response = await fetch("https://restcountries.eu/rest/v2/all")
            const results = await response.json()
            console.log(results)
            setCountries(results)
        }

    const setData = () => {
        fetchData()
    }

    return (
        <div className="searchCountryDiv">
            <div className="countryWrapper">
                <h1 className="searchTitle">Countries</h1>
                <p className="searchSubtitle">Please make sure to enter the country name in English</p>
                <div className="inputDiv">
                    <label className="searchLabel">Search by country name (In English)</label>
                    <input type="text" placeholder="eg. Colombia" className="searchInput"/>
                </div>
                <p className="or">Or</p>
                <button className="displayAll" onClick={setData}>Display all the countries</button>
            </div>
        </div>
    )
}

export default SearchCountry
