import useFetchApi from "../hooks/useFetchApi.js"
import "./countries.css"

const Countries = () => {

    const { data } = useFetchApi("https://restcountries.eu/rest/v2/all")
    const shortData = data.slice(1,60)
    console.log(shortData)

    return (
            <div className="mainDiv">
                {
                    shortData.map((country) => {
                        const { area, name, flag, capital } = country

                        return <>
                            <div className="countryDiv" key={area}>
                                <img src={flag} className="flag"></img>
                                <h3 className="countryName">{name}</h3>
                                <p className="countryCapital">Capital: {capital}</p>
                            </div>
                        </>
                    })
                    }
            </div>
    )
}

export default Countries
