import { useState, useEffect } from "react";

const useFetchApi = url => {

    const [photos, setPhotos] = useState([])

    const apiData = async () => {
        const res = await fetch(url)
        const { results } = await res.json()
        setPhotos(results)
        console.log(results)
    }

    useEffect(() => {
        apiData()
    },[])

    return { results }
}

export default UseFetchApi
