import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router'
import "./countryDetail.css"

const CountryDetail = () => {

    const countryData = useParams()
    const [singleCountryDetail, setSingleCountryDetail] = useState([])

    const fetchSingleCountry = async() => {

        const response = await fetch(`https://restcountries.eu/rest/v2/name/${countryData.name}`)
        const results = await response.json()
        setSingleCountryDetail(results[0])
    }

    console.log(singleCountryDetail)

    useEffect(() => {
        fetchSingleCountry()
    },[countryData])

    return (
        <div className="mainDiv">
            <div className="detailWrapper">
                <div className="detailCard">
                    <article>
                        <header className="headerDetail">
                            <h2 className="headerText">Country name: {singleCountryDetail.name}</h2>
                            <h2 className="headerText">Capital: {singleCountryDetail.capital}</h2>
                        </header>
                        <footer className="footerDetail">
                            <h3 className="footerText">Population: {singleCountryDetail.population}</h3>
                            <h3 className="footerText">Region: {singleCountryDetail.region}</h3>
                            <h3 className="footerText">Subregion: {singleCountryDetail.subregion}</h3>
                            <button className="covid">Covid Information</button>
                        </footer>
                    </article>    
                </div>
            </div>
        </div>
    )
}

export default CountryDetail
